    Validators.minLength(8)
  ]),
  sexe: new FormControl("",
  [
    Validators.required
  ]),
  checkcondicions: new FormControl("",
  [
    Validators.required
  ]),
  estat: new FormControl("",
  [
    Validators.required
  ])
})
submit(){
